import { createStore } from 'vuex'

export default createStore({
  state: {
    userData: []
  },

  mutations: {
    CREATE_USER(state, payload)
    {
      state.userData = payload;
    }
  },
  actions: {
    createUser({commit}, formData)
    {
     const signUpData = localStorage.setItem('createData', formData);
     commit('CREATE_USER', signUpData)
    }
  },
  getters: {
    getUser
  },
})
