    //  const emailVal = localStorage.getItem('userEmail');
    //  const passval = localStorage.getItem('userPassword');
    },
    createTask({commit}, task)
    {
      commit('CREATE_USER', task),
      localStorage.setItem('taskTitle', task.title);
      localStorage.setItem('taskDesc', task.desc);
      localStorage.setItem('taskId', task.id);

    },
    createTasks({ commit },state, task) {
      commit('CREATE_TASKS', task);
      localStorage.setItem('Tasks', JSON.stringify(state.userData));

    }
  },
  getters: {
    getUser(state)
    {
      return state.userData;
    }
  },
})
